package com.sajansthapit.virtual_power_plant.controller;import com.sajansthapit.virtual_power_plant.dto.request.BatteryFilterRequest;import com.sajansthapit.virtual_power_plant.dto.request.BatteryRequest;import com.sajansthapit.virtual_power_plant.dto.response.BatteryFilterResponse;import com.sajansthapit.virtual_power_plant.dto.response.BatterySaveResponse;import com.sajansthapit.virtual_power_plant.service.IBatteryService;import jakarta.validation.Valid;import lombok.AllArgsConstructor;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.validation.annotation.Validated;import org.springframework.web.bind.annotation.*;@Validated@RestController@AllArgsConstructor@RequestMapping("/api/battery/")public class BatteryController {    private final IBatteryService batteryService;    @PostMapping("/save-all")    public ResponseEntity<BatterySaveResponse> saveAll(@RequestBody @Valid BatteryRequest batteryRequest) {        BatterySaveResponse batterySaveResponse = batteryService.saveAll(batteryRequest);        return new ResponseEntity<>(batterySaveResponse, HttpStatus.CREATED);    }    @GetMapping("/filter")    public ResponseEntity<BatteryFilterResponse> filter(@RequestBody BatteryFilterRequest batteryFilterRequest) {        BatteryFilterResponse batteryFilterResponse = batteryService.filter(batteryFilterRequest);        return new ResponseEntity<>(batteryFilterResponse, HttpStatus.FOUND);    }}