package com.sajansthapit.virtual_power_plant.dto;import jakarta.validation.constraints.Min;import jakarta.validation.constraints.NotBlank;public record BatteryDto(        @NotBlank(message = "Please provide a name")        String name,        @NotBlank(message = "Please provide a postcode")        String postCode,        @Min(value = 0, message = "Watt Capacity must be greater than 0")        int wattCapacity) {}