package com.sajansthapit.virtual_power_plant.dto;import com.sajansthapit.virtual_power_plant.utils.Constants;import jakarta.validation.constraints.Min;import jakarta.validation.constraints.NotBlank;import jakarta.validation.constraints.Pattern;public record BatteryDto(        @NotBlank(message = "Please provide a name")        String name,        @NotBlank(message = "Please provide a postcode")        @Pattern(regexp = Constants.POSTCODE_PATTERN, message = "Postcode must contain 5 digits")        String postCode,        @Min(value = 0, message = "Watt Capacity must be greater than 0")        int wattCapacity) {}