package com.sajansthapit.virtual_power_plant.exception_handler;import com.sajansthapit.virtual_power_plant.exception_handler.exceptions.DuplicateBatteryException;import org.springframework.http.HttpStatus;import org.springframework.http.ProblemDetail;import org.springframework.web.bind.annotation.ExceptionHandler;import org.springframework.web.bind.annotation.RestControllerAdvice;@RestControllerAdvicepublic class RestExceptionHandler {    @ExceptionHandler(DuplicateBatteryException.class)    ProblemDetail handleDuplicateBatteryException(DuplicateBatteryException duplicateBatteryException){        return ProblemDetail.forStatusAndDetail(HttpStatus.BAD_REQUEST, duplicateBatteryException.getMessage());    }}