package com.sajansthapit.virtual_power_plant.model;import jakarta.persistence.Column;import jakarta.persistence.Entity;import jakarta.persistence.GeneratedValue;import jakarta.persistence.GenerationType;import jakarta.persistence.Id;import jakarta.persistence.SequenceGenerator;import jakarta.persistence.Table;import lombok.*;import org.hibernate.annotations.CreationTimestamp;import java.time.LocalDateTime;@Entity@Getter@Setter@Builder@ToString@EqualsAndHashCode@NoArgsConstructor@AllArgsConstructor@Table(name = "battery")public class Battery {    @Id    @GeneratedValue(strategy = GenerationType.SEQUENCE, generator = "battery_id_sequence")    @SequenceGenerator(name = "battery_id_sequence", sequenceName = "battery_id_sequence",  allocationSize=1)    @Column(name = "id")    private long id;    @Column(name = "name")    private String name;    @Column(name = "post_code")    private String postCode;    @Column(name = "watt_capacity")    private int wattCapacity;    @CreationTimestamp    @Column(name = "created_date")    private LocalDateTime createdDate;}