package com.sajansthapit.virtual_power_plant.repository;import com.sajansthapit.virtual_power_plant.model.Battery;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import org.springframework.data.repository.query.Param;import org.springframework.stereotype.Repository;import java.util.List;import java.util.Optional;@Repositorypublic interface BatteryRepository extends JpaRepository<Battery, Long> {    String POSTCODE_RANGE_QUERY = "SELECT b FROM Battery b WHERE CAST(b.postCode AS integer) BETWEEN :startPostCode AND :endPostCode";    Optional<Battery> findByNameAndPostCodeAndWattCapacity(String name, String postCode, int wattCapacity);    @Query(POSTCODE_RANGE_QUERY)    List<Battery> findByPostCodeRange(@Param("startPostCode") int startPostCode, @Param("endPostCode") int endPostCode);}