package com.sajansthapit.virtual_power_plant.service;import com.sajansthapit.virtual_power_plant.dto.request.BatteryFilterRequest;import com.sajansthapit.virtual_power_plant.dto.request.BatteryRequest;import com.sajansthapit.virtual_power_plant.dto.response.BatteryFilterResponse;import com.sajansthapit.virtual_power_plant.dto.response.BatterySaveResponse;public interface IBatteryService {    /**     * Method to persist list of battery to database     *     * @param batteryRequest: List of battery that contains name(String), postCode(String) and wattCapacity(int)     * @return BatterySaveResponse     */    BatterySaveResponse saveAll(BatteryRequest batteryRequest);    /**     * Method to filter battery by post code range     *     * @param batteryFilterRequest Post code range     * @return BatteryFilterResponse     */    BatteryFilterResponse filter(BatteryFilterRequest batteryFilterRequest);}