package com.sajansthapit.virtual_power_plant.service.impl;import com.sajansthapit.virtual_power_plant.dto.request.BatteryRequest;import com.sajansthapit.virtual_power_plant.dto.response.BatterySaveResponse;import com.sajansthapit.virtual_power_plant.model.Battery;import com.sajansthapit.virtual_power_plant.repository.BatteryRepository;import com.sajansthapit.virtual_power_plant.service.IBatteryService;import lombok.AllArgsConstructor;import org.springframework.stereotype.Service;import java.util.List;import java.util.stream.Collectors;@Service@AllArgsConstructorpublic class BatteryService implements IBatteryService {    private final BatteryRepository batteryRepository;    @Override    public BatterySaveResponse saveAll(BatteryRequest batteryRequest) {        List<Battery> batteries = batteryRequest.batteries().stream().map(battery ->                Battery.builder()                        .name(battery.name())                        .postCode(battery.postCode())                        .wattCapacity(battery.wattCapacity())                        .build())                .collect(Collectors.toList());        batteryRepository.saveAll(batteries);        return new BatterySaveResponse("Total of " + batteries.size() + " saved successfully");    }}